 */
export async function findAllMediaElementsAndPlay(cntx) {
  if (__Played__ == null) __Played__ = Symbol(uuidv4());
  const mediaElems = qsa(__MediaElementSelector__, cntx);
  if (mediaElems.length === 0) return false;
  const proms = [];
  let shouldWait = false;
  for (var i = 0; i < mediaElems.length; i++) {
    if (!mediaElems[i][__Played__]) {
      proms.push(noExceptPlayMediaElement(mediaElems[i]));
      Object.defineProperty(mediaElems[i], __Played__, {
        value: true,
        enumerable: false,
      });
      mediaElems[i][__Played__] = true;
      shouldWait = true;
    }
  }
  await Promise.all(proms);
  return shouldWait;
}

/**
 * Observe dom mutation using a MutationObserver as a stream (AsyncIterator)
 */
export class MutationStream {
  /**
   * Construct a new MutationStream
   */
  constructor() {
    /**
     * @type {MutationObserver}
     */
    this.mo = new MutationObserver((ml, ob) => {
      if (this._resolve) {
        this._resolve(ml);
      }
    });

    /**
     * @type {?function(arg: *): *}
     * @private
     */
    this._resolve = null;

    /**
     * @type {boolean}
     * @private
     */
    this._loopStream = false;
  }

  /**
   * Start observing an element for mutations
   * @param {Node} elem - The element to be observed for mutations
   * @param {Object} config - Configuration object accepted by mutation observers
   */
  observe(elem, config) {
    this.mo.observe(elem, config);
    this._loopStream = true;
  }

