      next = await Promise.race([
        this._getNext(),
        new Promise(resolve => {
          checkTo = setInterval(() => {
            if (stopPredicate()) {
              checkTo = null;
              clearInterval(checkTo);
              return resolve(null);
            }
          }, 1500);
        }),
